import { FETCH_USERS } from '../actions/admin';

const initialState = { users: [] };

const adminReducer = (state = initialState, action) => {
    switch (action.type) {
        case FETCH_USERS:
            return { ...state, users: action.payload };
        default:
            return state;
    }
};

export default adminReducer;
